create table passenger_profile (id bigint generated by default as identity, passengerlastname varchar(255), passengername varchar(255), passport_number varchar(255), user_profile_id bigint, primary key (id));
create table user_profile (id bigint generated by default as identity, email varchar(255), is_blocket_profile boolean, lastname varchar(255), phone integer, profilename varchar(255), user_id bigint, primary key (id));